import React, { useLayoutEffect } from "react";
import Header from "../components/Header";

export default function Give() {
  useLayoutEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <>
      <Header />
      <main>
        <section className="mt-20 p-4 md:p-10"></section>
        <h2 className="mt-10 text-6xl text-gray-800 text-center font-extrabold">
          Giving
        </h2>
        <p className="text-center mt-10 w-10/12 md:w-5/12 m-auto">
          We believe giving is an act of worship, and when practiced in
          cooperation with the Holy Spirit, we will become more like Christ.
          Though giving does sustain many practical functions of the church, its
          primary purpose is to align our hearts with Christ.
        </p>
        <div className="mt-20 w-10/12 md:w-4/12 m-auto p-5 border border-red-400">
          <form>
            <input
              type="text"
              placeholder="Full Name"
              required
              className="py-3 p-2"
            />
          </form>
        </div>
      </main>
    </>
  );
}
